import { Card, Paper ,Drawer,IconButton,Divider,List,ListItem} from "@material-ui/core";
import { OverlayTrigger, Button ,Popover} from "react-bootstrap";
import React, { useState } from "react";
import SmallCard from "../../cards/SmallCard";
import SummaryCards from "../../cards/SummaryCards";
import { ORDERS } from "../../staticData/SummaryData";
import { makeStyles, useTheme } from '@material-ui/core/styles';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';

const drawerWidth = 500;
const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  appBar: {
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginRight: drawerWidth,
  },
  title: {
    flexGrow: 1,
  },
  hide: {
    display: 'none',
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerHeader: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'flex-start',
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginRight: -drawerWidth,
  },
  contentShift: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginRight: 0,
  },
}));

export default function ConfirmedOrders(props) {
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);
  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };


  return (
    <div className="d-grid everypage right-grid" >
      <div className="gap-3" className={classes.drawerHeader} className={(classes.content, {
      [classes.contentShift]: open,
    })}>
        {ORDERS.map(() => {
          return (
            <button color="inherit"
            aria-label="open drawer"
            edge="end"
            onClick={handleDrawerOpen}>
              <Paper elevation={2} style={{ margin: "1rem" }}>
                <SummaryCards />
              </Paper>
            </button>
          );
        })}
      </div>
      <Drawer
     className={classes.drawer}
     variant="persistent"
     anchor="right"
     open={open}
     classes={{
       paper: classes.drawerPaper,
     }}
   >
     <div className={classes.drawerHeader}>
       <IconButton onClick={handleDrawerClose}>
         {theme.direction === 'rtl' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
       </IconButton>
     </div>
     <SmallCard/>
     <SmallCard/>

   </Drawer>
    </div>

  );
}
