import { Card, Paper } from '@material-ui/core'
import React, { useState } from 'react'
import SmallCard from '../../cards/SmallCard'
import SummaryCards from '../../cards/SummaryCards'
import {ORDERS} from "../../staticData/SummaryData"



export default function ConfirmedOrders(props) {
    const [show, setShow] = useState(false)
    function openRightPart(){
        setShow(true)
    }

    return (

        <div className={show ? 'd-grid everypage right-grid':"d-flex everypage right-grid"} >
            <div className="gap-3" >
                {ORDERS.map(()=>{return (<Paper elevation={2}><button style={{outline:"none",border:"none"}}><SummaryCards /></button></Paper>)})}

           {show && <div className="right-part m-3 p-3">
                <SmallCard/>
            </div>} 
            </div>
        </div>
    )
}
