import { Divider } from "@material-ui/core";
import React from "react";
import { Col, Row } from "react-bootstrap";

export default function SummaryCards() {
    const orders=[
        {
            customer_name:"lorem",
            customer_adrress:`House 4${<br/>}Ipsum Lane ${<br/>}Cannaught Place  , New Delhi ${<br/>} 498623`,
            order_id:"T shirt", 
            delivery_date:"27 Sept '21",
            method:"Pre Paid",
            products:[{
                name:"T shirt",
                price:"$799",
                brand:"Puma" 
            },
            {
                name:"Jeans",
                price:"$1199",
                brand:"Levis" 
            }
        ]

        }
    ]
  return (
    <div>
      <Row>
        <h6 className="ui-grey capital">product summary</h6>
      </Row>
      <Col xs={6}>
        <Row>
          <h5 className="ui-dark-grey capital">{orders[0].order_id}</h5>
          <br />
          <p className="primary-1 capital"> Delivery by date {orders[0].delivery_date}</p>
        </Row>
        {orders[0].products.map((product) => {
          return (
            <Row>
              <p className="ui-grey">{product.name}</p> <p className="ui-grey capital">{product.price}</p>
              <Divider/>
            </Row>

          );
        })}
        <Row>
            <p className="ui-dark-grey">Total</p> <p className="ui-dark-grey">total price calculated</p>
        </Row>
        <Row><p className="ui-black">{orders[0].method}</p></Row>
      </Col>
      <Col xs={6}>
        <Row>
            <p className="ui-grey capital">delivery partner</p>
        </Row>
        <Row>
            <p className="ui-dark-grey">Deliver to </p> <br/>
            <p className="ui-grey">
                {orders[0].customer_name} <br/>
                {orders[0].customer_address}
            </p>
        </Row>
      </Col>
    </div>
  );
}
