import React from "react";
import { DataGrid } from '@mui/x-data-grid';

export default function ReturnOrders() {
  const columns = [
    { field: "orderid", headerName: "Order ID" ,width: 200,},
    { field: "category", headerName: "Category" ,width: 150, sortable:false},
    { field: "brand", headerName: "Brand" ,width: 150,sortable:false},
    { field: "buyertype", headerName: "Buyer Type" ,width: 180,sortable:false},
    { field: "payment", headerName: "Payment",width: 210 },
    { field: "returnreason", headerName: "Return Reason" ,width: 200},
    { field: "additionalcomment", headerName: "Additional Comment",width: 200 },
    { field: "returnmoney", headerName: "Return Money",width: 200 },
    { field: "replacement", headerName: "Replacement" ,width: 200},
    { field: "status", headerName: "Status",width: 200 },
  ];
  const rows = [
    {
      id:1,
      orderid: 1983470230,
      category: "Clothing",
      brand: "Puma",
      buyertype: "Retailer",
      payment: "687",
      returnreason: "Out of Stock",
      additionalcomment: "The item will be restocked in a few days",
      returnmoney: "",
      replacement: "",
      status: "Warehouse",
    },
  ];

  return (
    <div style={{ height: 400, width: '100%' }} className="everypage">
    <DataGrid
      rows={rows}
      columns={columns}
      pageSize={5}
      rowsPerPageOptions={[5]}
      checkboxSelection
      disableSelectionOnClick
    />
  </div>
  );
}
