import {Paper,FormControl, InputLabel ,Select ,MenuItem} from '@material-ui/core'
import {makeStyles} from "@material-ui/core/styles"
import { Col, Image, Row ,Badge } from 'react-bootstrap'
import  {useState} from 'react'
import React  from 'react'

const useStyles = makeStyles((theme) => ({
    formControl: {
      margin: theme.spacing(1),
      minWidth: 120,
    },
    selectEmpty: {
      marginTop: theme.spacing(2),
    },
  }));
export default function SmallCard() {
    const [age, setAge] = useState('');
    const classes=useStyles()

  const handleChange = (event) => {
    setAge(event.target.value);
  }
    return (
        <Paper elevation={2} className="p-3" >
            <Row >
                <Badge bg="secondary">Best Seller</Badge>
            </Row>
            <Row>
            <Image rounded src="https://images.unsplash.com/photo-1618354691551-44de113f0164?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=658&q=80" />
            </Row>
            <Row className="mt-2">
                <Col>
                <span className="ui-black h3">Rs X</span><span className="ui-grey">/Piece</span><br/>
                <h5 className="ui-black pt-1">Product Name</h5>
                </Col>
                <Col>
                <span className="ui-grey">
                <FormControl variant="outlined" className={classes.formControl} >
        <InputLabel id="demo-simple-select-outlined-label">Age</InputLabel>
        <Select
          labelId="demo-simple-select-outlined-label"
          id="demo-simple-select-outlined"
          value={age}
          onChange={handleChange}
          label="Age"
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <MenuItem value={10}>Ten</MenuItem>
          <MenuItem value={20}>Twenty</MenuItem>
          <MenuItem value={30}>Thirty</MenuItem>
        </Select>
      </FormControl>

                    </span><br/>
                <span className="ui-grey">Qty:1</span>
                </Col>
            </Row>


        </Paper>
    )
}
