import { Card, Paper } from "@material-ui/core";
import { Dropdown, ButtonGroup } from "react-bootstrap";
import React, { useState } from "react";
import SmallCard from "../../cards/SmallCard";
import SummaryCards from "../../cards/SummaryCards";
import { ORDERS } from "../../staticData/SummaryData";

export default function ConfirmedOrders(props) {
  const [show, setShow] = useState(false);
  function openRightPart() {
    !show ? setShow(true) : setShow(false);
  }

  return (
    <div className="d-grid everypage right-grid">
      <div className="gap-3">
        {ORDERS.map((orders) => {
          return (

              <Dropdown
                as={ButtonGroup}
                key="end"
                id="dropdown-button-drop-end"
                drop="end"
                variant="secondary"
                title="Drop end"
              >
                  <Paper elevation={2} style={{ margin: "1rem" }}>
                <SummaryCards />
                </Paper>
                <Dropdown.Item eventKey="1"><SmallCard/></Dropdown.Item>
                <Dropdown.Item eventKey="2"><SmallCard/></Dropdown.Item>
                <Dropdown.Item eventKey="3"><SmallCard/></Dropdown.Item>
                <Dropdown.Item eventKey="4"><SmallCard/></Dropdown.Item>
              </Dropdown>

          );
        })}
      </div>
    </div>
  );
}
